SELECT TITLE, UB.BOARD_ID, UR.REPLY_ID, UR.WRITER_ID, UR.CONTENTS, DATE_FORMAT(UR.CREATED_DATE,'%Y-%m-%d') AS CREATED_DATE FROM USED_GOODS_BOARD AS UB JOIN USED_GOODS_REPLY AS UR ON UB.BOARD_ID = UR.BOARD_ID WHERE YEAR(UB.CREATED_DATE)=2022 AND MONTH(UB.CREATED_DATE)=10 ORDER BY UR.CREATED_DATE, UB.TITLE;